import React, { PureComponent } from 'react';
import LineChart from './LineChart';
import PieChart from './PieChart';
import AxesLineChart from './AxesLineChart';

class FlotCharts extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {  }
    }
    componentDidMount() {
        document.title = "Flot Charts";
        document.getElementById("pageTitle").innerHTML = "Flot Charts";
    }
    render() { 
        return ( 
            <React.Fragment>
                {/* <!-- /.row --> */}
                <div className="row">
                    <LineChart />
                    <PieChart />
                    <AxesLineChart />
                    <div className="col-lg-6">
                        <div className="panel panel-default">
                            <div className="panel-heading">
                                Moving Line Chart Example
                            </div>
                            {/* <!-- /.panel-heading --> */}
                            <div className="panel-body">
                                <div className="flot-chart">
                                    <div className="flot-chart-content" id="flot-line-chart-moving"></div>
                                </div>
                            </div>
                            {/* <!-- /.panel-body --> */}
                        </div>
                        {/* <!-- /.panel --> */}
                    </div>
                    {/* <!-- /.col-lg-6 --> */}
                    <div className="col-lg-6">
                        <div className="panel panel-default">
                            <div className="panel-heading">
                                Bar Chart Example
                            </div>
                            {/* <!-- /.panel-heading --> */}
                            <div className="panel-body">
                                <div className="flot-chart">
                                    <div className="flot-chart-content" id="flot-bar-chart"></div>
                                </div>
                            </div>
                            {/* <!-- /.panel-body --> */}
                        </div>
                        {/* <!-- /.panel --> */}
                    </div>
                    {/* <!-- /.col-lg-6 --> */}
                    <div className="col-lg-12">
                        <div className="panel panel-default">
                            <div className="panel-heading">
                                Flot Charts Usage
                            </div>
                            {/* <!-- /.panel-heading --> */}
                            <div className="panel-body">
                                <p>Flot is a pure JavaScript plotting library for jQuery, with a focus on simple usage, attractive looks, and interactive features. In SB Admin, we are using the most recent version of Flot along with a few plugins to enhance the user experience. The Flot plugins being used are the tooltip plugin for hoverable tooltips, and the resize plugin for fully responsive charts. The documentation for Flot Charts is available on their website, <a target="_blank" href="http://www.flotcharts.org/">http://www.flotcharts.org/</a>.</p>
                                <a target="_blank" className="btn btn-default btn-lg btn-block" href="http://www.flotcharts.org/">View Flot Charts Documentation</a>
                            </div>
                            {/* <!-- /.panel-body --> */}
                        </div>
                        {/* <!-- /.panel --> */}
                    </div>
                    {/* <!-- /.col-lg-6 --> */}
                </div>
                {/* <!-- /.row --> */}
            </React.Fragment>
         );
    }
}

export default FlotCharts;
